// 引入Vue，调用use()时要用到
import Vue from 'vue'
// 引入Vuex，创建Store对象
import Vuex from 'vuex'

// 准备actions
const actions = {
    // actions中存储着一个个的方法（处理业务逻辑），方法有两个参数，第一个参数为上下文参数，第二个参数为dispatch()传递的值。
    // 上下文参数一般用context表示，
    increment: function (context,value) {
        cs
    }
}
// 准备mutations
const mutations = {}
// 准备state
const state = {
    n:0
}

// 使用Vuex
Vue.use(Vuex)

// 创建Store对象
export default new Vuex.Store({
    actions: actions, // 键和值同名时可以触发对象的简写形式
    mutations,
    state
})
