// 该文件专门用于创建整个应用的路由器
import VueRouter from 'vue-router'
//引入组件
import About from '../pages/About.vue'
import Home from '../pages/Home.vue'
import News from '../pages/News.vue'
import Message from '../pages/Message.vue'
import Detail from '../pages/Detail.vue'

// 创建一个路由器
// 这里需要设置全局路由守卫所以定义一个变量接收路由器，设置完了再暴露出去
const router =  new VueRouter({
	// 路由配置项routes，值是一个数组，数组中的元素是一个个的路由配置对象
	routes: [
		{
			name:'about',
			path:'/about',
			component: About,
			// meta配置项，它的值是一个对象，我们自定义的一些属性就放在该配置项里面，比如权限
			meta:{title:'关于'}
			
		},
		{
			name:'home',
			path:'/home',
			component: Home,
			children: [
				{
					name:'news',
					path: 'news',
					component: News,
					meta:{isAuth:true,title:'新闻'}	// 自定义一个isAuth属性，表示是否授权
				},
				{
					name:'message',
					path: 'message',
					component: Message,
					children: [
						{
							name: 'detail',	
							path: 'detail',	
							component: Detail,
							meta:{isAuth:true,title:'详情'},
							props($route){
								return {
									// 可以获取query参数以props的形式传递
									id:$route.query.id,
									title: $route.query.title,
									// 可以传自定义参数
									a:1,
									b:'hello'
								}
							}
						},
						
					]
				}
			]
		}
	]
})

// 设置全局前置路由守卫	--- 初始化时被调用、每次切换路由之前被调用
// 该方法有三个参数，第一个参数表示要跳转的路由，第二个参数表示从哪个路由跳转，第三个参数为放行函数
router.beforeEach(to, from, next => {
	console.log('前置路由守卫',to,from)
	if (to.meta.isAuth) { //判断是否有权限
		// 这里从浏览器本地存储中获取数据，若没有获取到则表示没有权限
		if(localStorage.getItem('school')==='hello'){
			next()
		}else{
			alert('学校名不对，无权限查看！')
		}
	}else{
		next()
	}
})



// 该方法是VueRouter构造函数原型上的方法

// 暴露路由器
export default router
