import Vue from 'vue'
//引入Vuex
import Vuex from 'vuex'
//引入模块
import countOptions from './count'
import wordsOptions from './words'
//应用Vuex插件
Vue.use(Vuex)

//创建并暴露store
export default new Vuex.Store({
	// modules：模块配置项
	// 包含多个 module，一个 module 是一个 store 的配置对象，与一个组件（包含有共享数据）对应
	modules: {
		// 键指的是模块的名称，这里也可以用对象的简写形式
		countAbout:countOptions,
		wordsAbout:wordsOptions
		// 这些模块的state中的数据最终也会在store对象的state属性中
		// 不过那些属性是存在『它们所在模块名称命名』的对象中，该对象存在store对象的state属性中。
		// 例如countAbout:{
		// 		sum:0,
		// 		school:
		// }
	}
})
